import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { context } from "../context/CartContextProvider";
import "./cart.css";
import { db } from "../../firebase/firebase";
import {
  collection,
  addDoc,
  serverTimestamp,
} from "firebase/firestore";
import FormCart from "./formCart";

const Cart = () => {
 
  const { productsCart, removeProducts } = useContext(context);
  const [sectionCheckout, setSectionCheckout] = useState(true);
  console.log(productsCart)
  const productsDetailToBuyer = productsCart.map((product, i) => {
    return {
      price: product.price,
      id: product.id,
      title: product.title,
      amount: product.amount,
    };
  });
  const totalOfCart = productsCart.reduce(function (count, object) {
    return count + object.price * object.amount;
  }, 0);

  const checkOut = (personalData) => {
    const docOrder = collection(db, "orders");
    addDoc(docOrder, {
      personalData,
      productsDetailToBuyer,
      date: serverTimestamp(),
      total: totalOfCart,
    })
  };

  
  const goToCheckout = () => {
    setSectionCheckout(false);
  };


  return (


    <>

       {productsCart.length === 0 ? 
        <Link to="/">
          <h1 className="sectionCenterCart">
            ¡No hay productos! Vuelve a tus compras
          </h1>
        </Link>
       :

    <main class="containerCart">

<h1 class="heading">
  <ion-icon name="cart-outline"></ion-icon> Shopping Cart
</h1>

<div class="item-flex">

  {/* <!--
   - checkout section
  --> */}
<FormCart checkOut={checkOut} />

  {/* <!--
    - cart section
  --> */ }
  <section class="cart">

    <div class="cart-item-box">

      <h2 class="section-heading">Order Summery</h2>

      {productsCart.map((product, i) => {
        return(

          <div className="product-card">

        
        <div className="card1">

          <div className="img-box">
            <img src={product.image} alt="Green tomatoes" width="80px" className="product-img imgCart"/>
          </div>

          <div className="detail">

            <h4 className="product-name">{product.title}</h4>

            <div className="wrapper">

              <div className="product-qty">
                <button id="decrement buttonCart">
                  <ion-icon name="remove-outline"></ion-icon>
                </button>

                <span id="quantity">{product.amount}</span>

                <button id="increment buttonCart">
                  <ion-icon name="add-outline"></ion-icon>
                </button>
              </div>

              <div class="price">
                $ <span id="price">{product.price}</span>
              </div>

            </div>

          </div>

          <button className ="product-close-btn buttonCart">
            <ion-icon name="close-outline"></ion-icon>
          </button>

        </div>

      </div>

)
})}
   

    </div>

    <div class="wrapper">

      <div class="amount">

        <div class="subtotal">
          <span>Subtotal</span> <span>$ <span id="subtotal">{totalOfCart}</span></span>
        </div>
        <div class="shipping">
          <span>Envio</span> <span>$ <span id="shipping">{10.00}
        </span></span>
        </div>

        <div class="total">
          <span>Total</span> <span>$ <span id="total">{totalOfCart + 10.00}</span></span>
        </div>

      </div>

    </div>

  </section>

</div>

</main>
}
    </>

  );
};

export default Cart;















