import { useState, useContext } from "react";
import "./formCart.css"
import { context } from "../context/CartContextProvider"
import { CheckOutlined } from "@mui/icons-material";

const FormCart = ({getPersonalData, checkOut}) => {

    const {removeAllProducts} = useContext(context);
    
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [mail, setMail] = useState("");
    const [personalData, setPersonalData] = useState("");



    const finish = () =>{
        checkOut()
        removeAllProducts();

    }

    const personalDataConstructor = () =>{
        
        setPersonalData({
            nombre: nombre,
            apellido: apellido,
            mail: mail,
        })  
        getPersonalData(personalData)
    }

    console.log(personalData)
    
    return(

        <div className="widthForm">
        <h1>Datos de facturaci√≥n</h1>
            <form action="" onChange={personalDataConstructor}>
                <div>
                <input className="inputWidthcontrols" type="text" id="nombre" name="nombre" 
                value={nombre} placeholder="Ingresa tu nombre"
                onChange={(e)=>setNombre(e.target.value)} />
                </div>
                <div>
                <input className="inputWidthcontrols" type="text" id="apellido" name="apellido" 
                value={apellido} placeholder="Ingresa tu Apellido"
                onChange={(e)=>setApellido(e.target.value)} />
                </div>
                <div>
                <input className="inputWidthcontrols" type="text" id="mail" name="mail" 
                value={mail} placeholder="Ingresa tu Mail"
                onChange={(e)=>setMail(e.target.value)} />
                </div>
            </form>
            <div>
            <button className="buttonCard" onClick={finish} >Comprar</button>
        </div>
        </div>

    )
}

export default FormCart
