import React, { useState, useEffect, useContext } from "react";
import "../ItemDetailContainer/itemDetail.css"
import ItemCount from "../ItemDetailContainer/ItemCount";
import { CircularProgress } from "@mui/material";
import { Link } from "react-router-dom";
import { context } from "../context/CartContextProvider"


const ItemDetail =({product})=>{

    const [image, setImage] = useState(true);
    const [count, setCount] = useState(1);
    const { addProductsCart } = useContext(context);
    
    
    const countPushToState = (countOriginal) =>{
        setCount(countOriginal)
    }
    
    const promise = new Promise((resolve, reject)=>{
        setTimeout(()=>{
            resolve(product.image) 
        },1000)});


    useEffect(()=>{
        promise.then (() => setImage(false))});
        
        return(   
            
            <div class = "main-wrapper">
        <div class = "container">
            <div class = "product-div">
                <div class = "product-div-left">
                    <div class = "img-container">
                        <img src = {product.image} alt = "watch"/>
                    </div>
   
                </div>
                <div class = "product-div-right">
                    <span class = "product-name">{product.title}</span>
                    <span class = "product-price">{product.price}</span>
                    <div class = "product-rating">
                        <span><i class = "fas fa-star"></i></span>
                        <span><i class = "fas fa-star"></i></span>
                        <span><i class = "fas fa-star"></i></span>
                        <span><i class = "fas fa-star"></i></span>
                        <span><i class = "fas fa-star-half-alt"></i></span>
                        <span>(350 ratings)</span>
                    </div>
                    <p class = "product-description">Lorem ipsum dolor, sit amet consectetur adipisicing elit. Vitae animi ad minima veritatis dolore. Architecto facere dignissimos voluptate fugit ratione molestias quis quidem exercitationem voluptas.</p>
                    <div class = "btn-groups">
                        <button type = "button" class = "add-cart-btn"><i class = "fas fa-shopping-cart"></i>add to cart</button>
                        <button type = "button" class = "buy-now-btn"><i class = "fas fa-wallet"></i>buy now</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
         
        )

}

export default ItemDetail


