import { useState } from "react";
import "./formCart.css"

const FormCart = ({getPersonalData}) => {

    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [mail, setMail] = useState("");
    const [personalData, setPersonalData] = useState("");

    const finish = () =>{
        getPersonalData(personalData)
    }

    const personalDataConstructor = () =>{
        setPersonalData({
            nombre: nombre,
            apellido: apellido,
            mail: mail,

        })  
    }

    
    return(
    <>
        <div className="widthForm">
        <h1>Datos de facturaci√≥n</h1>
            <form action="" onChange={personalDataConstructor}>
                <div>
                <input type="text" id="nombre" name="nombre" 
                value={nombre} placeholder="Ingresa tu nombre"
                onChange={(e)=>setNombre(e.target.value)} />
                </div>
                <div>
                <input type="text" id="apellido" name="apellido" 
                value={apellido} placeholder="Ingresa tu Apellido"
                onChange={(e)=>setApellido(e.target.value)} />
                </div>
                <div>
                <input type="text" id="mail" name="mail" 
                value={mail} placeholder="Ingresa tu Mail"
                onChange={(e)=>setMail(e.target.value)} />
                </div>
            </form>
            <div>
            <button onClick={finish} >Comprar</button>
        </div>
        </div>

    </>

    )
}

export default FormCart
