import React, { useState, useEffect, useContext } from "react";
import "../ItemDetailContainer/itemDetail.css"
import ItemCount from "../ItemDetailContainer/ItemCount";
import { CircularProgress } from "@mui/material";
import { Link } from "react-router-dom";
import { context } from "../context/CartContextProvider"
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';


const ItemDetail =({product})=>{

    const [image, setImage] = useState(true);
    const [count, setCount] = useState(1);
    const { addProductsCart } = useContext(context);
    
    
    const countPushToState = (countOriginal) =>{
        setCount(countOriginal)
    }
    
    const promise = new Promise((resolve, reject)=>{
        setTimeout(()=>{
            resolve(product.image) 
        },1000)});


    useEffect(()=>{
        promise.then (() => setImage(false))});
        
        return(   
            
            <div class = "main-wrapper">
        <div class = "container">
            <div class = "product-div">
                <div class = "product-div-left">
                    <div class = "img-container">
                        <img src = {product.image} alt = "watch" className="imgDetail"/>
                    </div>
   
                </div>
                <div class = "product-div-right">
                    <span class = "product-name">{product.title}</span>
                    <span class = "product-price">${product.price}</span>
                    <div class = "product-rating">
                    <div>
                {product.rating >= 1 ? <StarIcon className="ratingOfItem"/> : <StarBorderIcon className="ratingOfItem"/>}
                {product.rating >= 2 ? <StarIcon className="ratingOfItem"/> : <StarBorderIcon className="ratingOfItem"/>}
                {product.rating >= 3 ? <StarIcon className="ratingOfItem"/> : <StarBorderIcon className="ratingOfItem"/>}
                {product.rating >= 4 ? <StarIcon className="ratingOfItem"/> : <StarBorderIcon className="ratingOfItem"/>}
                {product.rating >= 5 ? <StarIcon className="ratingOfItem"/> : <StarBorderIcon className="ratingOfItem"/>}
            </div>
                        <span>({product.rating})</span>
                    </div>
                    <p class = "product-description">{product.description}</p>
                    <div class = "btn-groups">
                    <ItemCount stock={product.stock}  countPushToState={countPushToState}/>
                        <button type = "button" class = "add-cart-btn">add to cart</button>
                        <Link to={`/cart`}> <button className="buttonDetail" onClick={()=> addProductsCart(product, count)}> ¡Comprar! </button> </Link>
                    </div>
                </div>
            </div>
        </div>
    </div>
         
        )

}

export default ItemDetail


